DROP TABLE IF EXISTS `escort_waypoints`;
CREATE TABLE `escort_waypoints` (
  `entry` mediumint(8) unsigned NOT NULL default '0' COMMENT 'creature_template entry',
  `pointid` mediumint(8) unsigned NOT NULL default '0',
  `location_x` float NOT NULL default '0',
  `location_y` float NOT NULL default '0',
  `location_z` float NOT NULL default '0',
  `waittime` int(10) unsigned NOT NULL default '0' COMMENT 'in milliseconds',
  `point_comment` text,
  PRIMARY KEY (`entry`, `pointid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=FIXED COMMENT='Creature waypoints for escort events';

DELETE FROM escort_waypoints WHERE entry=21027;
