INSERT INTO creature_text VALUES
(15362, 0, 0, "Les murs de la pièce tremblent. Il se passe quelque chose...", 3, 0, 100, 0, 0, 0, ''),
(15362, 1, 0, "Malfurion Stormrage s'incline.", 2, 0, 100, 2, 0, 0, ''),
(15362, 2, 0, "Soyez ferme, champion. Je sais pourquoi vous êtes ici et ce que vous cherchez. Eranikus n'abandonnera pas facilement son fragment. Il a été perverti... perverti par la même force que vous cherchez à détruire.", 0, 0, 100, 1, 0, 0, ''),
(15362, 3, 0, "Êtes-vous réellement surpris ? Est-il si difficile de croire que la puissance d'un Ancien Dieu puisse s'étendre jusqu'à l'intérieur du Rêve ? C'est vrai, Eranikus, le tyran du Rêve, mène une bataille contre nous tous. Le Cauchemar le suit dans son sillage de destruction.", 0, 0, 100, 1, 0, 0, ''),
(15362, 4, 0, "Comprenez ceci, Eranikus ne veut rien de plus qu'être amené en Azeroth depuis le Rêve. Une fois qu'il sera ici, il ne s'arrêtera devant rien pour détruire ma manifestation physique. Cependant, ceci est le seul moyen de récupérer le fragment de sceptre.", 0, 0, 100, 1, 0, 0, ''),
(15362, 5, 0, "Vous le ramènerez dans ce monde, champion.", 0, 0, 100, 1, 0, 0, '');
