INSERT INTO script_texts (entry, content_default, content_loc2, type, language, comment) VALUES
(-2100023, "Children of the Night, heed your Queen's call!", "Enfants de la Nuit, entendez l'appel de votre Reine !", 1, 0, "Sylvanas - Wickerman Festival 1"),
(-2100024, "I join you in celebration of this most revered of nights - the night we Forsaken broke the Scourge's yoke of oppression!", "Je vous rejoins dans la célébration de cette nuit des plus fêtées - la nuit où les Réprouvés ont brisé l'oppression des Mort-vivants !", 1, 0, "Sylvanas - Wickerman Festival 2"),
(-2100025, "It is this night that our enemies fear us the most. It is THIS night that we show our enemies what it means to stand against the Forsaken!", "C'est cette nuit que nos ennemis craignent le plus. C'est CETTE NUIT que nous montrons à nos ennemis ce qu'implique d'être opposé aux Réprouvés !", 1, 0, "Sylvanas - Wickerman Festival 3"),
(-2100026, "We burn the effigy of the Wickerman as a symbol of our struggle against those who would oppose us. We wear the ashes of the burnt Wickerman as a symbol of our neverending fight against those who would enslave us.", "Nous brûlons cette effigie de l'Homme d'osier comme symbole de notre lutte contre ceux qui se mettent en travers de notre chemin. Nous portons les cendres de l'Homme d'osier brûlé comme symbole de notre lutte sans fin contre ceux qui nous voudraient nous enchaîner.", 1, 0, "Sylvanas - Wickerman Festival 4"),
(-2100027, "Now is the time to shake the world to its foundations! NOW is the time to remind those who would enslave us that we shall never yield!", "Il est maintenant temps de faire trembler le monde sur ses bases ! MAINTENANT, il est temps de rappeler à ceux qui voudraient nous enchaîner que jamais nous ne cèderons !", 1, 0, "Sylvanas - Wickerman Festival 5"),
(-2100028, "NOW is the time of the Forsaken! Power to the Forsaken - NOW AND FOREVER!!!", "MAINTENANT est venu le temps des Réprouvés ! Pouvoir aux Réprouvés - MAINTENANT ET POUR TOUJOURS !!!", 1, 0, "Sylvanas - Wickerman Festival 6");
