UPDATE locales_quest SET OfferRewardText_loc2 = "Par le Puits de Soleil ! C'est la troisième estafette que nous avons perdue ce mois-ci.$B$BJ'apprécie le courage dont vous avez fait preuve en vous aventurant dans l'Allée du Point-du-Jour. Les gardes se doivent de garder un oeil sur ces déshérités.", RequestItemsText_loc2 = "Qu'avez-vous trouvé, $N ?" WHERE entry = 9705;
