DELETE FROM script_texts WHERE entry BETWEEN -1976006 AND -1976000;
INSERT INTO script_texts (entry, content_default, content_loc2, type, comment) VALUES (-1976000, "Bow down! Kneel before the Ashbringer! A new dawn approaches, brothers and sisters! Our message will be delivered to the filth of this world through the chosen one!", "A genoux ! A genoux devant le Porte-Cendre ! Une nouvelle aube approche, mes frères et mes soeurs ! Notre message sera délivré à la racaille de ce monde à travers l'élu !", 1, "Ashbringer - Monastery"), (-1976001, "You hold my father's blade, $N. My soldiers are yours to control, my $GLord:Lady;. Take them... Lead them... The impure must be purged. They must be cleansed of their taint.", "Vous portez l'épée de mon père, $N. Mes soldats sont sous votre contrôle, $GSeigneur:Ma Dame;. Prenez-les... Guidez-les... Les impurs doivent être purifiés. Ils doivent être nettoyés de leur souillure.", 0, "Ashbringer - Monastery"), (-1976002, "Renault...", "Renault...", 0, "Ashbringer - Monastery"), (-1976003, "Father... But... How?", "Père... Mais... Comment ?", 0, "Ashbringer - Monastery"), (-1976004, "Did you think that your betrayal would be forgotten? Lost in the carefully planned cover up of my death? Blood of my blood, the blade felt your cruelty long after my heart had stopped beating. And in death, I knew what you had done. But now, the chains of Kel'Thuzad hold me no more. I come to serve justice. I AM ASHBRINGER.", "Pensais-tu que ta trahison allait être oubliée ? Perdue dans le plan longtemps préparé pour camoufler ma mort ? Sang de mon sang, l'épée a senti ta cruauté bien longtemps après que mon coeur ait cessé de battre. Et dans la mort, j'ai su ce que tu avais fait. Mais maintenant, les chaînes de Kel'thuzad ne me retiennent plus. Je viens pour servir la justice. JE SUIS LE PORTE-CENDRE.", 0, "Ashbringer - Monastery"), (-1976005, "Forgive me, father! Please...", "Pardonnez-moi, père ! S'il vous plait...", 0, "Ashbringer - Monastery"), (-1976006, "You are forgiven...", "Tu es pardonné...", 0, "Ashbringer - Monastery");

