update locales_quest set offerrewardtext_loc2 = "Ah, $N ! Je ne pensais que vous auriez beaucoup de mal à trouver l'endroit, mes directions sont excellentes, vous savez... $B$BLaissez-moi terminer mon rapport.$B$B<Il prend du papier et écrit un moment dessus.>$B$BVoilà ! Haha, voilà qui est amusant... Je suppose, $N, que celà ne vous dérangerait pas de faire une dernière chose pour moi ?", requestitemstext_loc2 = "Vous ne pouvez pas le trouver ? Mes directions étaient très claires ! Nous n'avons pas beaucoup de temps, vous devez vous dépêcher ! Ne revenez pas tant que vous n'avez pas trouvé la grotte." where entry = 287;
update locales_quest set offerrewardtext_loc2 = "Pour moi ? Vous êtes un vrai héros, $N !", requestitemstext_loc2= "Hmph ! Il y a tout cet alcool là-bas. Mais j'ai reçu des ordres stricts, je ne dois pas y toucher. Si seulement je pouvais goûter un peu de notre tonneblonde... Celà me ferait du bien !" where entry=308;
update locales_quest set offerrewardtext_loc2 = "Pas mal, $N ! Avez-vous eu du mal à passer leurs gardes ?$B$BAucune importance. Un grand merci et si jamais vous avez besoin d'un fût, n'oubliez pas les Brasselorge !", requestitemstext_loc2 = "Avez-vous procédé à l'échange ?" where entry = 311;
update locales_quest set offerrewardtext_loc2 = "Quelle âme généreuse vous êtes, $N ! Grâce à votre bravoure je ne mourrai pas de faim lorsque la saison froide frappera.", requestitemstext_loc2 = "Avez-vous réussi à accéder à mon placard à viande, $N ?" where entry = 312;
UPDATE locales_quest SET RequestItemsText_loc2 = "Avez-vous pris soin de ces animaux sauvages ?" WHERE entry = 319;
UPDATE locales_quest SET RequestItemsText_loc2 = "Si nous pouvons éradiquer les troggs de la carrière de Gol'Bolar, ce sera la première d'une longue liste d'étapes pour nous débarasser de cette menace." WHERE entry = 433;
