INSERT INTO creature_text (entry, groupid, id, text_en, text_fr, probability, comment) VALUES (11711, 0, 0, "Wait... did you hear that? Something approaches from the west!", "Attendez... avez-vous entendu ça ? Quelque chose vient de l'ouest !", 100, "Sentinel Aynasha - TALK_QUESTACCEPT"), (11711, 1, 0, "Praise Elune! I don't know if I could have survived the day without you, friend.", "Louée soit Élune ! Je ne sais pas si j’aurais pu survivre sans votre aide.", 100, "Sentinel Aynasha - TALK_QUESTFINISHED1"), (11711, 2, 0, "My leg feels much better now, the remedy must be working. If you will excuse me, I must go report to my superiors about what has transpired here.", "Ma jambe va beaucoup mieux maintenant, le remède doit être en train de faire effet. Excusez-moi, je dois maintenant aller faire mon rapport auprès de mes supérieurs et leur expliquer ce qui se passe ici.", 100, "Sentinel Aynasha - TALK_QUESTFINISHED2"), (11711, 3, 0, "Please speak with Sentinel Onaeya at Maestra's Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day.", "Veuillez parler à la Sentinelle Onaeya au Poste de Maestra, dans Orneval, elle veillera à ce que vous receviez une récompense à la hauteur de la bravoure dont vous avez fait preuve.", 100, "Sentinel Aynasha - TALK_QUESTFINISHED3");

