DELETE FROM creature_text WHERE entry BETWEEN 23418 AND 23420;
INSERT INTO creature_text VALUES (23418, 0, 0, "Pain and suffering are all that await you!", "Douleur et souffrance, voilà tout ce qui vous attend !", 1, 0, 100, 0, 0, 11415, "TALK_SUFF_SAY_FREED"), (23418, 1, 0, "Don't leave me alone!", "Ne me laissez pas seul !", 1, 0, 100, 0, 0, 11416, "TALK_SUFF_SAY_AGGRO"), (23418, 2, 0, "Look at what you make me do!", "Regardez ce que vous m'avez fait faire !", 1, 0, 100, 0, 0, 11417, "TALK_SUFF_SAY_SLAY"), (23418, 2, 1, "I didn't ask for this!", "Je n'ai pas demandé ça !", 1, 0, 100, 0, 0, 11418, "TALK_SUFF_SAY_SLAY"), (23418, 2, 2, "The pain is only beginning!", "La douleur ne fait que commencer !", 1, 0, 100, 0, 0, 11419, "TALK_SUFF_SAY_SLAY"), (23418, 3, 0, "I don't want to go back!", "Je ne veux pas y retourner !", 1, 0, 100, 0, 0, 11420, "TALK_SUFF_SAY_RECAP"), (23418, 4, 0, "Now what do I do?", "Qu'est ce que je fais maintenant ?", 1, 0, 100, 0, 0, 11421, "TALK_SUFF_SAY_AFTER"), (23418, 5, 0, "Essence of Suffering becomes enraged!", "Essence de la Souffrance devient enragé !", 3, 0, 100, 0, 0, 0, "TALK_SUFF_EMOTE_ENRAGE");
INSERT INTO creature_text VALUES (23419, 0, 0, "You can have anything you desire... for a price.", "Vous pouvez avoir tout ce que vous désirez... en y mettant le prix.", 1, 0, 100, 0, 0, 11408, "TALK_DESI_SAY_FREED"), (23419, 1, 0, "Fulfilment is at hand!", "Le bonheur est à portée de main.", 1, 0, 100, 0, 0, 11409, "TALK_DESI_SAY_SLAY"), (23419, 1, 1, "Yes... you'll stay with us now...", "Oui ! Vous allez rester avec nous maintenant.", 1, 0, 100, 0, 0, 11410, "TALK_DESI_SAY_SLAY"), (23419, 1, 2, "Your reach exceeds your grasp.", "Vous avez les yeux plus gros que le ventre.", 1, 0, 100, 0, 0, 11412, "TALK_DESI_SAY_SLAY"), (23419, 2, 0, "Be careful what you wish for...", "Méfiez-vous de ce que vous désirez.", 1, 0, 100, 0, 0, 11411, "TALK_DESI_SAY_SPEC"), (23419, 3, 0, "I'll be waiting...", "Je vous attendrai...", 1, 0, 100, 0, 0, 11413, "TALK_DESI_SAY_RECAP"), (23419, 4, 0, "I won't be far...", "Je ne serai jamais loin...", 1, 0, 100, 0, 0, 11414, "TALK_DESI_SAY_AFTER");
INSERT INTO creature_text VALUES (23420, 0, 0, "Beware: I live!", "Prenez garde : je suis vivant !", 1, 0, 100, 0, 0, 11399, "TALK_ANGER_SAY_FREED"), (23420, 0, 1, "So... foolish.", "C'est... stupide.", 1, 0, 100, 0, 0, 11400, "TALK_ANGER_SAY_FREED"), (23420, 1, 0, "<maniacal cackle>", "<rire machiavélique>", 1, 0, 100, 0, 0, 11401, "TALK_ANGER_SAY_SLAY"), (23420, 1, 1, "Enough. No more.", "Assez ! Terminé !", 1, 0, 100, 0, 0, 11402, "TALK_ANGER_SAY_SLAY"), (23420, 2, 0, "On your knees!", "A genoux !", 1, 0, 100, 0, 0, 11403, "TALK_ANGER_SAY_SPEC"), (23420, 3, 0, "Beware, coward.", "Prenez garde, pleutre !", 1, 0, 100, 0, 0, 11405, "TALK_ANGER_SAY_BEFORE"), (23420, 4, 0, "I won't... be... ignored.", "Je ne vous... laisserez pas... m'oublier !", 1, 0, 100, 0, 0, 11404, "TALK_ANGER_SAY_DEATH");

