INSERT INTO script_texts (entry, content_default, content_loc2) VALUES (-1600393, "Torta, my love! I have returned at long last.", "Torta, mon amour ! Je suis revenu, enfin."), (-1600394, "You have any idea how long I've been waiting here? And where's dinner? All that time gone and nothing to show for it?", "As-tu une idée du temps que j'ai passé à t'attendre ? Et où est le dîner ? Tu es parti tout ce temps et tu n'as rien ramené ?"), (-1600395, "My dearest Torta, I have been gone for so long. Finally we are reunited. At long last our love can blossom again.", "Ma chère Torta, je suis parti si longtemps. Finalement nous sommes réunis. Notre amour peut fleurir à nouveau."), (-1600396, "Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.", "Assez divagué. Je suis affamée ! Maintenant, traîne ta carapace poussiéreuse jusqu'à l'océan et ramène de quoi manger."), (-1600397, "Yes, Torta. Whatever your heart desires...", "Oui Torta, tout ce que ton coeur désire..."), (-1600398, "And try not to get lost this time....", "Et essaye de ne pas te perdre cette fois...");
