UPDATE locales_npc_text SET Text0_0_loc2 = "Il était temps que la guilde nous envoie un peu de sang neuf ! C'est dommage que vous ayez fait tout ce chemin pour rien. Nous n'avons pas le droit de monter un spectacle pour nos invités, le croiriez-vous ? Moroes nous a interdit de commencer un nouveau spectacle, et mes commanditaires deviennent fous ! Il faut vraiment que l'on fasse quelque chose au sujet de Moroes." WHERE entry = 8978;
